Vue.component("prefs",{template:`<div class='prefs'>
               <label>API Key</label>
               <input type='text'
                 id="apiKey"
                 v-on:change=propChanged($event)
                 v-bind:value=preferences.apiKey />
             </div>`,props:["preferences"],methods:{propChanged:function(n){this.$emit("pref-changed",n.target.id,n.target.value)}}});var app=new Vue({el:"#app",data:{preferences:""},created:function(){let n=window.localStorage.getItem("preferences");this.preferences=JSON.parse(n)||{}},methods:{savePreferences:function(n,t){n&&(this.preferences[n]=t,window.localStorage.setItem("preferences",JSON.stringify(this.preferences)))}}});