Vue.component("inputTag",InputTag);Vue.component("prefs",{template:`<div class='prefs'>
               <div>
                 <label>API Key</label>
                 <input type='text'
                   id="apiKey"
                   v-on:change=propChanged($event)
                   v-bind:value=preferences.apiKey />
               </div>
               <div>
                 <label>Categories</label>
                 <input-tag :on-change="categoryChanged" :tags="preferences.categories"></input-tag>
               </div>
             </div>`,props:["preferences"],methods:{propChanged:function(n){this.$emit("pref-changed",n.target.id,n.target.value)},categoryChanged:function(n){this.$emit("pref-changed","categories",n)}}});var app=new Vue({el:"#app",data:{preferences:""},created:function(){let n=window.localStorage.getItem("preferences");this.preferences=JSON.parse(n)||{categories:[]}},methods:{savePreferences:function(n,t){n&&(this.preferences[n]=t,window.localStorage.setItem("preferences",JSON.stringify(this.preferences)))}}});